frappe.ui.form.on('Employee Grievance', {
    refresh: function(frm) {
        function toggle_fields() {
            if (frappe.user.has_role('Employee')) {
                if (frm.doc.workflow_state === 'Draft') {
                    frm.toggle_display(['investigation_details_section','resolution_details_section','custom_actions_taken'], true);
                }
            }
            if (frappe.user.has_role('HR Manager')) {
                if (frm.doc.workflow_state === 'Submitted to HR') {
                    frm.toggle_display(['investigation_details_section','resolution_details_section','custom_actions_taken'], true);
                }
            }
        }

        toggle_fields();

        frm.fields_dict.workflow_state.$input.on('change', function() {
            toggle_fields();
        });
    },
    before_save: function(frm) {
        if (frappe.user.has_role('Employee')) {
            frm.toggle_display(['investigation_details_section','resolution_details_section','custom_actions_taken'], false);
        }
