frappe.ui.form.on('Timesheet Submission', {
    month_year: function(frm) {
        const monthYear = frm.doc.month_year;
        const monthYearPattern = /^(0[1-9]|1[0-2])-(\d{4})$/;
        if (monthYear && !monthYearPattern.test(monthYear)) {
            frappe.show_alert({
                message: __('Please enter Month-Year in MM-YYYY format.'),
                indicator: 'orange'
            });
        }
    },
    onload: function(frm) {
        if (!frm.doc.month_year) {
            frm.set_value('month_year', moment().format('MM-YYYY'));
        }
    },
    before_save: function(frm) {
        const monthYear = frm.doc.month_year;
        const monthYearPattern = /^(0[1-9]|1[0-2])-(\d{4})$/;
        if (monthYear && !monthYearPattern.test(monthYear)) {
            frappe.msgprint(__('Please enter Month-Year in MM-YYYY format.'));
            frappe.validated = false;  // Prevent form from being saved
        }
    }
});

frappe.ui.form.on('Employee', {
    refresh: function(frm) {
        frappe.call({
            method: 'corporate_services.api.role.get_role.get_user_roles',
            args: {
                user: frappe.session.user
            },
            callback: function(response) {
                const roles = response.message || [];
                console.log('User Roles:', roles);

                const isHRUser = roles.includes('HR');
                const isFinanceUser = roles.includes('Finance');
                const isCurrentUser = frappe.session.user === frm.doc.user_id;

                const fieldsToToggle = [
                    'marital_status',
                    'family_background', 'blood_group', 'health_details', 
                    'custom_pwds', 'custom_specify_the_pwds',
                    'health_insurance_provider', 'health_insurance_no',
                    'passport_number', 'valid_upto', 'date_of_issue',
                    'place_of_issue', 'custom_name_of_the_body',
                    'custom_membership_status', 'custom_date_of_membership',
                    'custom_membership_no',
                    'custom_employee_confidential_information',
                    'custom_attache_documents',
                    'custom_next_of_kin',
                    'custom_beneficiary',
                    'custom_beneficiary_relation',
                    'address_section',
                ];

                const sectionsToToggle = [
                    'custom_salary_section', 
                    'bank_details_section', 
                    'custom_contract_terms',
                    'custom_contract_terms_section',

                ];

                // Helper function to toggle visibility
                const toggleFieldVisibility = (fields, visibility) => {
                    fields.forEach(field => {
                        frm.set_df_property(field, 'hidden', !visibility);
                    });
                };

                // Toggle sections based on HR, Finance, or current user
                const canViewSalaryAndBankDetails = isHRUser || isFinanceUser || isCurrentUser;
                toggleFieldVisibility(sectionsToToggle, canViewSalaryAndBankDetails);

                // Toggle other fields based on HR or current user
                const canViewFields = isHRUser || isCurrentUser;
                toggleFieldVisibility(fieldsToToggle, canViewFields);
            },
            error: function(err) {
                console.error("Error fetching roles:", err);
            }
        });
    }
});

frappe.ui.form.on('Project', {
    refresh: function(frm) {
        frappe.call({
            method: 'corporate_services.api.projects.budget_tracker_analysis.budget_calc',
            args: {
                docname: frm.doc.name
            },
            callback: function(r) {
                if (!r.exc) {
                    frm.clear_table('custom_budget_tracker_analysis');
                    if (r.message && r.message.projects && r.message.projects.length > 0) {
                        r.message.timesheets.forEach(function(project) {
                            let child = frm.add_child('custom_budget_tracker_analysis');
                            child.rate = project.rate;
                            child.daysunits = project.daysunits;
                        });
                        frm.refresh_field('custom_budget_tracker_analysis');
                    } else {
                        console.log('No burn rate found');
                    }
                } else {
                    frappe.msgprint(__('There was an error fetching budgets'));
                    console.error('Error fetching burn rate:', r.exc);
                }
            }
        });
    }
});

