    },
    total_consumed_material_cost: function(frm) {
        calculate_total_expenses(frm);
    }
});

function calculate_total_expenses(frm) {
    let total_expenses = 
        (frm.doc.total_costing_amount || 0) +
        (frm.doc.total_expense_claim || 0) +
        (frm.doc.total_purchase_cost || 0) +
        (frm.doc.total_consumed_material_cost || 0);

    frm.set_value('custom_actual_expenses', total_expenses);

    // frm.save();
}

frappe.ui.form.on('Project', {
    refresh: function(frm) {
        // Calculate the total amount received for the project
        frappe.call({
            method: 'frappe.client.get_list',
            args: {
                doctype: 'Payment Entry',
                filters: {
                    'project': frm.doc.name,
                    'docstatus': 1
                },
                fields: ['paid_amount']
            },
            callback: function(r) {
                if (r.message) {
                    const total_received = r.message.reduce((acc, row) => acc + row.paid_amount, 0);
                    frm.set_value('custom_total_amount_received', total_received);

                    // Calculate custom_funds_received_balance if custom_actual_expenses is defined
                    if (frm.doc.custom_actual_expenses !== undefined) {
                        const funds_received_balance = total_received - frm.doc.custom_actual_expenses;
                        frm.set_value('custom_funds_received_balance', funds_received_balance);

                        // Calculate custom_burn_rate
                        const burn_rate = frm.doc.custom_actual_expenses / total_received;
                        frm.set_value('custom_burn_rate', burn_rate);
                    }
                }
            }
        });
    },

    // Trigger calculations when custom_actual_expenses changes
    custom_actual_expenses: function(frm) {
        if (frm.doc.custom_total_amount_received !== undefined) {
            // Update custom_funds_received_balance
            const funds_received_balance = frm.doc.custom_total_amount_received - frm.doc.custom_actual_expenses;
            frm.set_value('custom_funds_received_balance', funds_received_balance);

            // Update custom_overall_budget_balance if custom_cost is defined
            if (frm.doc.custom_cost !== undefined) {
                const overall_budget_balance = frm.doc.custom_cost - frm.doc.custom_actual_expenses;
                frm.set_value('custom_overall_budget_balance', overall_budget_balance);

                // Calculate custom_overall_project_burn_rate
                const overall_burn_rate = frm.doc.custom_actual_expenses / frm.doc.custom_cost;
                frm.set_value('custom_overall_project_burn_rate', overall_burn_rate);
            }

            // Update custom_burn_rate
            const burn_rate = frm.doc.custom_actual_expenses / frm.doc.custom_total_amount_received;
            frm.set_value('custom_burn_rate', burn_rate);
        }
    },

    // Trigger cost and balance calculations when custom_rate changes
    custom_rate: function(frm) {
        if (frm.doc.custom_days !== undefined) {
            const cost = frm.doc.custom_rate * frm.doc.custom_days;
            frm.set_value('custom_cost', cost);

            // Calculate overall budget balance if custom_actual_expenses is defined
            if (frm.doc.custom_actual_expenses !== undefined) {
                const overall_budget_balance = cost - frm.doc.custom_actual_expenses;
                frm.set_value('custom_overall_budget_balance', overall_budget_balance);

                // Calculate custom_overall_project_burn_rate
                const overall_burn_rate = frm.doc.custom_actual_expenses / cost;
                frm.set_value('custom_overall_project_burn_rate', overall_burn_rate);
            }
        }
    },

    // Trigger cost and balance calculations when custom_days changes
    custom_days: function(frm) {
        if (frm.doc.custom_rate !== undefined) {
            const cost = frm.doc.custom_rate * frm.doc.custom_days;
            frm.set_value('custom_cost', cost);

            // Calculate overall budget balance if custom_actual_expenses is defined
            if (frm.doc.custom_actual_expenses !== undefined) {
                const overall_budget_balance = cost - frm.doc.custom_actual_expenses;
                frm.set_value('custom_overall_budget_balance', overall_budget_balance);

                // Calculate custom_overall_project_burn_rate
                const overall_burn_rate = frm.doc.custom_actual_expenses / cost;
                frm.set_value('custom_overall_project_burn_rate', overall_burn_rate);
            }
        }
    }
});
