    if (frm.doc.employee && frm.doc.month_year && !frm.is_new()) {
        frm.set_df_property('download_template', 'hidden', 0);
        frm.set_df_property('timesheet', 'hidden', 0);
    } else {
        frm.set_df_property('download_template', 'hidden', 1);
        frm.set_df_property('timesheet', 'hidden', 1);
    }
}

