frappe.ui.form.on('Timesheet Submission', {
    refresh: function(frm) {
        frm.add_custom_button(__('Import Timesheet'), function() {
            if (frm.doc.timesheet) {
                if (!frm.doc.timesheet_imported) {
                    frappe.call({
                        method: 'corporate_services.api.timesheet.timesheet_import.timesheet_import',
                        args: {
                            docname: frm.doc.name
                        },
                        callback: function(response) {
                            if (response.message === 'success') {
                                // Add the success message here
                                frappe.msgprint(__('Employee timesheet created successfully.'));
                                frm.set_value('timesheet_imported', 1); 
                                fetch_timesheets_for_employee(frm);
                                frm.save();
                            } else {
                                frappe.msgprint(__('There was an error creating the employee timesheet.'));
                            }
                        }
                    });
                } else {
                    frappe.msgprint(__('Timesheet has already been imported.'));
                    fetch_timesheets_for_employee(frm);
                }
            } else {
                frappe.msgprint(__('Please upload a timesheet file before importing.'));
            }
        }, __('Actions'));
    }
});

function fetch_timesheets_for_employee(frm) {
    frappe.call({
        method: 'corporate_services.api.timesheet.timesheet_fetch.fetch_timesheets_for_employee',
        args: {
            employee_id: frm.doc.employee,
            month: frm.doc.month_year,
            docname: frm.doc.name
        },
        callback: function(r) {
            if (!r.exc) {
                frm.clear_table('timesheet_per_project');
                if (r.message && r.message.timesheets && r.message.timesheets.length > 0) {
                    r.message.timesheets.forEach(function(timesheet) {
                        let child = frm.add_child('timesheet_per_project');
                        child.timesheet = timesheet.name;
                        child.total_hours = timesheet.total_hours;
                        child.project = timesheet.parent_project;
                        child.month = timesheet.custom_month;
                        child.percent = timesheet.percent_pay;
                        child.pay = timesheet.pay_for_project;
                        child.timesheet_type = timesheet.custom_timesheet_type;
                        child.status = timesheet.status;
                    });
                    frm.refresh_field('timesheet_per_project');

                    frm.set_value('total_working_hours', r.message.total_working_hours);
                } else {
                    frappe.msgprint(__('No timesheets found for the employee.'));
                }
            } else {
                frappe.msgprint(__('There was an error fetching timesheets.'));
            }
        }
    });
}

frappe.ui.form.on('Timesheet Submission', {
    refresh: function(frm) {
        frm.fields_dict.download_template.$input.on('click', function() {
            frappe.call({
                method: "corporate_services.api.timesheet.timesheet_generation_export.timesheet_generation_export",
                args: {
                    docname: frm.doc.name
                },
                callback: function(r) {
                    if (r.message && typeof r.message === 'string' && r.message !== "error") {
                        var download_url = r.message;
                        window.open(download_url, '_blank');

                    } else if (r.message && typeof r.message === 'object') {
                        var download_url = r.message.file_url;
                        window.open(download_url, '_blank');

                    } else {
                        frappe.msgprint(__("Failed to generate CSV file. Please check logs for details."));
                    }
                }
            });
        });
    }
});

