});

frappe.ui.form.on('Leave Application', {
    from_date: function(frm) {
        // Check if the leave type is Annual Leave
        if (frm.doc.leave_type === 'Annual Leave') {
            const noticePeriod = frm.doc.annual_leave_notice_period || 7;
            const minDate = frappe.datetime.add_days(frappe.datetime.get_today(), noticePeriod);
            if (frm.doc.from_date < minDate) {
                frappe.msgprint("You must apply for Annual Leave at least " + noticePeriod + " days in advance.");
                frm.set_value('from_date', '')
                frappe.validated = false;
            }
        }
    }
});

