frappe.ui.form.on('Timesheet Submission', {
    refresh: function(frm) {
        function toggle_fields() {
            if (frappe.user.has_role('Employee')) {
                if (frm.doc.workflow_state === 'Draft') {
                    frm.toggle_display(['internal_monthly_timesheet','timesheet_per_project'], true);
                }else {
                    frm.toggle_display(['internal_monthly_timesheet','timesheet_per_project'], true);
                }
            }
            if (frappe.user.has_role('Finance')) {
                if (frm.doc.workflow_state === 'Submitted to HR') {
                    frm.toggle_display(['internal_monthly_timesheet','timesheet_per_project'], true);
                }
            }
        }

        toggle_fields();

        frm.fields_dict.workflow_state.$input.on('change', function() {
            toggle_fields();
        });
    },
    before_save: function(frm) {
        if (frappe.user.has_role('Employee')) {
            frm.toggle_display(['internal_monthly_timesheet','timesheet_per_project'], false);
        }
    }
});

frappe.ui.form.on('Timesheet Submission', {
    month_year: function(frm) {
        const monthYear = frm.doc.month_year;
        const monthYearPattern = /^(0[1-9]|1[0-2])-(\d{4})$/;
        if (monthYear && !monthYearPattern.test(monthYear)) {
            frappe.show_alert({
                message: __('Please enter Month-Year in MM-YYYY format.'),
                indicator: 'orange'
            });
        }
    },
    onload: function(frm) {
        if (!frm.doc.month_year) {
            frm.set_value('month_year', moment().format('MM-YYYY'));
        }
    },
    before_save: function(frm) {
        const monthYear = frm.doc.month_year;
        const monthYearPattern = /^(0[1-9]|1[0-2])-(\d{4})$/;
        if (monthYear && !monthYearPattern.test(monthYear)) {
            frappe.msgprint(__('Please enter Month-Year in MM-YYYY format.'));
            frappe.validated = false;
        }
    }
});

frappe.ui.form.on('Employee', {
    refresh: function(frm) {
        frappe.call({
            method: 'corporate_services.api.role.get_role.get_user_roles',
            args: {
                user: frappe.session.user
            },
            callback: function(response) {
                const roles = response.message || [];
                console.log('User Roles:', roles);

                const isHRUser = roles.includes('HR Manager');
                const isFinanceUser = roles.includes('Finance');
                const isCurrentUser = frappe.session.user === frm.doc.user_id;

                const fieldsToToggle = [
                    'marital_status',
                    'family_background', 'blood_group', 'health_details', 
                    'custom_pwds', 'custom_specify_the_pwds',
                    'health_insurance_provider', 'health_insurance_no',
                    'passport_number', 'valid_upto', 'date_of_issue',
                    'place_of_issue', 'custom_name_of_the_body',
                    'custom_membership_status', 'custom_date_of_membership',
                    'custom_membership_no',
                    'custom_employee_confidential_information',
                    'custom_attache_documents',
                    'custom_next_of_kin',
                    'custom_beneficiary',
                    'custom_beneficiary_relation',
                    'address_section',
                ];

                const sectionsToToggle = [
                    'custom_salary_section', 
                    'bank_details_section', 
                    'custom_contract_terms',
                    'custom_contract_terms_section',

                ];

                // Helper function to toggle visibility
                const toggleFieldVisibility = (fields, visibility) => {
                    fields.forEach(field => {
                        frm.set_df_property(field, 'hidden', !visibility);
                    });
                };

                // Toggle sections based on HR, Finance, or current user
                const canViewSalaryAndBankDetails = isHRUser || isFinanceUser || isCurrentUser;
                toggleFieldVisibility(sectionsToToggle, canViewSalaryAndBankDetails);

                // Toggle other fields based on HR or current user
                const canViewFields = isHRUser || isCurrentUser;
                toggleFieldVisibility(fieldsToToggle, canViewFields);
            },
            error: function(err) {
                console.error("Error fetching roles:", err);
            }
        });
    }
});

frappe.ui.form.on('Project', {
    refresh: function(frm) {
        // Calculate the total amount received for the project
        frappe.call({
            method: 'frappe.client.get_list',
            args: {
                doctype: 'Payment Entry',
                filters: {
                    'project': frm.doc.name,
                    'docstatus': 1
                },
                fields: ['paid_amount']
            },
            callback: function(r) {
                if (r.message) {
                    const total_received = r.message.reduce((acc, row) => acc + row.paid_amount, 0);
                    frm.set_value('custom_total_amount_received', total_received);

                    // Calculate custom_funds_received_balance if custom_actual_expenses is defined
                    if (frm.doc.custom_actual_expenses !== undefined) {
                        const funds_received_balance = total_received - frm.doc.custom_actual_expenses;
                        frm.set_value('custom_funds_received_balance', funds_received_balance);

                        // Calculate custom_burn_rate
                        const burn_rate = frm.doc.custom_actual_expenses / total_received;
                        frm.set_value('custom_burn_rate', burn_rate);
                    }
                }
            }
        });
    },

    // Trigger calculations when custom_actual_expenses changes
    custom_actual_expenses: function(frm) {
        if (frm.doc.custom_total_amount_received !== undefined) {
            // Update custom_funds_received_balance
            const funds_received_balance = frm.doc.custom_total_amount_received - frm.doc.custom_actual_expenses;
            frm.set_value('custom_funds_received_balance', funds_received_balance);

            // Update custom_overall_budget_balance if custom_cost is defined
            if (frm.doc.custom_cost !== undefined) {
                const overall_budget_balance = frm.doc.custom_cost - frm.doc.custom_actual_expenses;
                frm.set_value('custom_overall_budget_balance', overall_budget_balance);

                // Calculate custom_overall_project_burn_rate
                const overall_burn_rate = frm.doc.custom_actual_expenses / frm.doc.custom_cost;
                frm.set_value('custom_overall_project_burn_rate', overall_burn_rate);
            }

            // Update custom_burn_rate
            const burn_rate = frm.doc.custom_actual_expenses / frm.doc.custom_total_amount_received;
            frm.set_value('custom_burn_rate', burn_rate);
        }
    },

    // Trigger cost and balance calculations when custom_rate changes
    custom_rate: function(frm) {
        if (frm.doc.custom_days !== undefined) {
            const cost = frm.doc.custom_rate * frm.doc.custom_days;
            frm.set_value('custom_cost', cost);

            // Calculate overall budget balance if custom_actual_expenses is defined
            if (frm.doc.custom_actual_expenses !== undefined) {
                const overall_budget_balance = cost - frm.doc.custom_actual_expenses;
                frm.set_value('custom_overall_budget_balance', overall_budget_balance);

                // Calculate custom_overall_project_burn_rate
                const overall_burn_rate = frm.doc.custom_actual_expenses / cost;
                frm.set_value('custom_overall_project_burn_rate', overall_burn_rate);
            }
        }
    },

    // Trigger cost and balance calculations when custom_days changes
    custom_days: function(frm) {
        if (frm.doc.custom_rate !== undefined) {
            const cost = frm.doc.custom_rate * frm.doc.custom_days;
            frm.set_value('custom_cost', cost);

            // Calculate overall budget balance if custom_actual_expenses is defined
            if (frm.doc.custom_actual_expenses !== undefined) {
                const overall_budget_balance = cost - frm.doc.custom_actual_expenses;
                frm.set_value('custom_overall_budget_balance', overall_budget_balance);

                // Calculate custom_overall_project_burn_rate
                const overall_burn_rate = frm.doc.custom_actual_expenses / cost;
                frm.set_value('custom_overall_project_burn_rate', overall_burn_rate);
            }
        }
    }
});

