frappe.ui.form.on('Budget Line Template', {
    unit: function(frm, cdt, cdn) {
        calculate_cost(frm, cdt, cdn);
    },
    rate: function(frm, cdt, cdn) {
        calculate_cost(frm, cdt, cdn);
    },
    budget_line_template_remove: function(frm, cdt, cdn) {
        calculate_total_cost(frm);
    },
    budget_line_template_add: function(frm, cdt, cdn) {
        calculate_total_cost(frm);
    }
});

function calculate_cost(frm, cdt, cdn) {
    let row = locals[cdt][cdn];
    let unit_loe = flt(row.unit_loe) || 0;
    let rate = flt(row.rate) || 0;

    frappe.model.set_value(cdt, cdn, 'cost', flt(unit_loe * rate))
        .then(() => calculate_total_cost(frm));
}

function calculate_total_cost(frm) {
    let custom_total_cost = 0;
    if(frm.doc.custom_budget_line_details) {
        custom_total_cost = frm.doc.custom_budget_line_details.reduce((total, row) => {
            return total + (flt(row.cost) || 0);
        }, 0);
    }

    frm.set_value('custom_total_cost', flt(custom_total_cost));
}

