frappe.ui.form.on('Travel Request', {
    onload: function(frm) {
        frm.set_df_property("custom_icl_proposed_transport_matrix", "hidden", 1);
    },

    refresh: function(frm) {
        if (!frm.doc.custom_local_travel || frm.doc.custom_local_travel == 0) {
            frm.set_df_property("custom_icl_proposed_transport_matrix", "hidden", 1);
        }
    },

    custom_place_of_travel_per_diem: function(frm) {
        if (frm.doc.custom_place_of_travel_per_diem) {
            frm.set_df_property("custom_per_diem_rates", "hidden", 0);

            frappe.call({
                method: "corporate_services.api.travel_request.travel_request.get_per_diem_rates",
                args: {
                    place: frm.doc.custom_place_of_travel_per_diem
                },
                callback: function(r) {
                    if (r.message) {
                        frm.clear_table("custom_per_diem_rates");

                        r.message.forEach(item => {
                            let row = frm.add_child("custom_per_diem_rates");
                            row.place_of_travel = item.place_of_travel;
                            row.accommodation_max = item.accommodation_max;
                            row.accommodation_min = item.accommodation_min;
                            row.meal_incidentals_expenses_max = item.meal_incidentals_expenses_max;
                            row.meal_incidentals_expenses_min = item.meal_incidentals_expenses_min;
                        });

                        frm.refresh_field("custom_per_diem_rates");
                    }
                }
            });
        } else {
            frm.set_df_property("custom_per_diem_rates", "hidden", 1);
            frm.clear_table("custom_per_diem_rates");
            frm.refresh_field("custom_per_diem_rates");
        }
    },

    custom_local_travel: function(frm) {
        if (frm.doc.custom_local_travel == 1) {
            frm.set_df_property("custom_icl_proposed_transport_matrix", "hidden", 0);

            frappe.call({
                method: "corporate_services.api.travel_request.travel_request.get_local_per_diem_rates",

                callback: function(r) {
                    if (r.message) {
                        frm.clear_table("custom_icl_proposed_transport_matrix");

                        r.message.forEach(item => {
                            let row = frm.add_child("custom_icl_proposed_transport_matrix");
                            row.from = item.from;
                            row.to_countries = item.to_countries;
                            row.province = item.province;
                            row.transport_means_of_travel = item.transport_means_of_travel;
                            row.fares = item.fares;
                            row.m_i = item.m_i;
                            row.accommodation = item.accommodation;
                        });

                        frm.refresh_field("custom_icl_proposed_transport_matrix");
                    }
                }
            });
        } else {
            frm.set_df_property("custom_icl_proposed_transport_matrix", "hidden", 1);
            frm.clear_table("custom_icl_proposed_transport_matrix");
            frm.refresh_field("custom_icl_proposed_transport_matrix");
        }
    }
});

frappe.ui.form.on('Travel Request Activity Participants', {
    hotel_accomodation: function(frm, cdt, cdn) {
        calculate_totals(frm);
    },
    meals_incedentals: function(frm, cdt, cdn) {
        calculate_totals(frm);
    },
    fx_rate: function(frm, cdt, cdn) {
        calculate_totals(frm);
    },
    total: function(frm, cdt, cdn) {
        calculate_totals(frm);
    }
});


function calculate_totals(frm) {
    let grand_total = 0;
    let grand_total_local_currency = 0;

    frm.doc.custom_activity_participants_table.forEach(row => {
        row.total = (parseFloat(row.hotel_accomodation) || 0) + (parseFloat(row.meals_incedentals) || 0);

        row.local_currency = (row.total || 0) * (parseFloat(row.fx_rate) || 1);

        grand_total += row.total;

        grand_total_local_currency += row.local_currency;
    });


    const formatted_grand_total = new Intl.NumberFormat().format(grand_total);
    const formatted_grand_total_local_currency = new Intl.NumberFormat().format(grand_total_local_currency);

    frm.set_value("custom_expected_support", formatted_grand_total);
    frm.set_value("custom_amount_in_local_currency", formatted_grand_total_local_currency);

    frm.refresh_field("custom_activity_participants_table");
    frm.refresh_field("custom_expected_support");
    frm.refresh_field("custom_amount_in_local_currency");

