function get_currency_symbol(frm) {
    let currency_symbol = '';
    if (frm.doc.currency) {
        frappe.call({
            method: 'frappe.client.get',
            args: {
                doctype: 'Currency',
                name: frm.doc.currency
            },
            async: false,
            callback: function(response) {
                if (response.message) {
                    currency_symbol = response.message.symbol;
                }
            }
        });
    }
    return currency_symbol;
}

function format_currency_value(symbol, value) {
    return symbol + " " + (parseFloat(value) || 0).toFixed(2);
}
function calculate_totals(frm) {
    let total_spent = 0;
    let total_amount_local = 0;

    if (frm.doc.activity_participants_reconciliation) {
        frm.doc.activity_participants_reconciliation.forEach(row => {
            total_spent += parseFloat(row.total_paid) || 0;
            total_amount_local += parseFloat(row.amount_in_local_currency) || 0;
        });
    }

    let currency_symbol = get_currency_symbol(frm);

    frappe.model.set_value(frm.doctype, frm.docname, 'total_spent', `${currency_symbol} ${format_number(total_spent)}`)
        .then(() => {
            return frappe.model.set_value(frm.doctype, frm.docname, 'total_local_amount', format_number(total_amount_local));
        })
        .then(() => {
            let total_advance = 0;

            if (frm.doc.currency === 'KES') {
                total_advance = parseFloat(frm.doc.local_total_advance) || 0;
            } else {
                total_advance = get_number_value(frm.doc.total_advance);
            }

            return frappe.model.set_value(frm.doctype, frm.docname, 'total_advance', `${currency_symbol} ${format_number(total_advance)}`);
        })
        .then(() => {
            let total_advance = get_number_value(frm.doc.total_advance);
            let total_spent = get_number_value(frm.doc.total_spent);
            let balance = total_advance - total_spent;


            return frappe.model.set_value(frm.doctype, frm.docname, 'total_balance', `${currency_symbol} ${format_number(balance)}`);
        });
}

function format_number(value) {
    if (!value) value = 0;
    return (parseFloat(value).toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 2 }));
}

function get_number_value(value) {
    if (!value) return 0;
    return parseFloat(value.toString().replace(/[^\d.-]/g, "")) || 0;
}

