    },
    unit_loe: function(frm, cdt, cdn) {
        calculate_cost(frm, cdt, cdn);
    },
    rate: function(frm, cdt, cdn) {
        calculate_cost(frm, cdt, cdn);
    }
});

function calculate_cost(frm, cdt, cdn) {
    let row = locals[cdt][cdn];
    row.cost = parseFloat(row.unit_loe) * parseFloat(row.rate) || 0;

    // Calculate the total cost for the specific budget line type
    let total_cost = 0;
    frm.doc.budget_line_template.forEach(function(d) {
        if (d.budget_line === row.budget_line) {
            total_cost += parseFloat(d.cost) || 0;
        }
    });

    // Update the Budget Template Summary child table
    let summary_row = frm.doc.budget_template_summary.find(d => d.budget_line === row.budget_line);
    if (summary_row) {
        summary_row.cost = total_cost.toLocaleString();
    } else {
        frm.add_child('budget_template_summary', {
            budget_line: row.budget_line,
            cost: total_cost
        });
    }

    // Refresh the fields to show the updated values
    frm.refresh_field('budget_line_template');
    frm.refresh_field('budget_template_summary');

    // Calculate indirect cost
    calculate_indirect_cost(frm);
}

function calculate_indirect_cost(frm) {
    let total_cost = 0;
    frm.doc.budget_line_template.forEach(function(d) {
        total_cost += parseFloat(d.cost) || 0;
    });

    let indirect_cost_rate = parseFloat(frm.doc.indirect_cost_rate) || 0;
    let indirect_cost = total_cost * (indirect_cost_rate / 100);
    frm.set_value('indirect_cost_on_all_costs', indirect_cost.toLocaleString());

    // Refresh the field to show the updated value
    frm.refresh_field('indirect_cost_on_all_costs');
}

