frappe.ui.form.on('Asset Damage Loss Theft Report Form', {
    onload: function(frm) {
        // Configure date picker to disable future dates for incident_date and reported_on
        frm.set_df_property('incident_date', 'datepicker', {
            maxDate: frappe.datetime.nowdate()
        });

        frm.set_df_property('reported_on', 'datepicker', {
            maxDate: frappe.datetime.nowdate()
        });
    },

    // Real-time validation for incident_date
    incident_date: function(frm) {
        // Check if incident_date is in the future
        if (frm.doc.incident_date && frappe.datetime.get_diff(frm.doc.incident_date, frappe.datetime.nowdate()) > 0) {
            frappe.throw(__('Incident Date cannot be a future date.'));
        }
    },

    // Real-time validation for reported_on
    reported_on: function(frm) {
        // Check if reported_on is before incident_date
        if (frm.doc.reported_on && frm.doc.incident_date && frappe.datetime.get_diff(frm.doc.reported_on, frm.doc.incident_date) < 0) {
            frappe.throw(__('Reported On date cannot be before Incident Date.'));
        }

        // Check if reported_on is in the future
        if (frm.doc.reported_on && frappe.datetime.get_diff(frm.doc.reported_on, frappe.datetime.nowdate()) > 0) {
            frappe.throw(__('Reported On date cannot be a future date.'));
        }
    }
});
